import React, { useState } from "react";
import data from "./component/data";
import Review from "./component/Review";

const App = () => {
   const [index, setIndex] = useState(0);
   const { name, url, job, quote } = data[index];
   const Increment=()=>{
      setIndex(index+1)
      console.log(index)
   }
   return (
      <div className="app">
         <div className="review_div">
            <h2>Our Review</h2>
            <hr />
            <Review name={name} url={url} job={job} quote={quote} />
            <div className="btn__div">
                <button><i class="fa fa-chevron-left" aria-hidden="true"></i></button>
                <button onClick={Increment}><i class="fa fa-chevron-right" aria-hidden="true"></i></button>
            </div>
         </div>
      </div>
   );
};

export default App;
