import React, { useState } from 'react'
import data from "./component/"

const App = () => {
    const [index,setIndex]=useState(0)
    const {name,url,job,quote}=data[index];
    return (
        <div className="app">
        <div className="review_div">
            <h2>Our Review</h2>
            <hr/>
            <div className="review">
                <div className="img__sec">
                    <img src={url}/>
                </div>
            </div>
        </div >
            
        </div>
    )
}

export default App
