import React from "react";
import ReactDOM from "react-dom";
import "./index.css";

let CurrentDate = new Date();
CurrentDate = CurrentDate.getHours();

let greetings = "";
const Style = {};

if (CurrentDate >= 1 && CurrentDate < 12) {
    greetings = "Good morning";
    Style.color = "green";

} else if (CurrentDate >= 12 && CurrentDate <= 16) {
    greetings = "Good Afternoon";
    Style.color = "brown";

} else if (CurrentDate >= 17 && CurrentDate <= 23) {
    greetings = "Good Night";
    Style.color = "black";

}


const divStyle = {
    width: "100%",
    height: "100vh",
    backgroundColor: "yellow",
    display: "grid",
    placeItems: "center"
}
ReactDOM.render(
    <>
        <div style={divStyle}>
            <div className="inner">
                <h1>Hello sir,<span style={Style}>{greetings}</span></h1>
            </div>
        </div>
    </>, document.getElementById("root")

);
